mapper.select:
SELECT coalesce(sum(met_impressions), 0)
FROM adv_db.metrics
WHERE met_daily = #{daily}

SELECT coalesce(sum(met_clicks) , 0)
FROM adv_db.metrics
         JOIN adv_db.campaign ON met_cpg_id = cpg_id
         JOIN adv_db.datasource ON ds_id = cpg_ds_id
WHERE cpg_name = #{campaign}
  AND ds_name = #{dataSource}

SELECT coalesce(sum(met_clicks) , 0)
FROM adv_db.metrics
JOIN adv_db.campaign ON met_cpg_id = cpg_id
JOIN adv_db.datasource ON ds_id = cpg_ds_id
WHERE ds_name = #{dataSource}
AND met_daily >= #{startDate}

    AND met_daily <= #{endDate}
